import { combineReducers } from 'redux'

const initialState = {
  menuselected: VisibilityFilters.SHOW_ALL,
  todos: []
}

export const rootReducer   = (state, action) => {
  switch (action.type) {
    case "TOGGLE_TODO":
      if(action.id !== undefined){
        return state.menuselected=  action.id;//, viewselected:"Grid"}
      }else{
        state = {menuselected: "Staff", viewselected:"Grid"};
       return state;
       }
    break;
    case "TOGGLE_VIEW":
    //  alert(action.id)
      alert(JSON.stringify(state))
      if(action.id !== undefined){
        state.viewselected = action.id;
        alert(JSON.stringify(state))
        return state;// = {viewselected: action.id}
      }else{
        state.viewselected = "Grid";
        alert(JSON.stringify(state))
       return state;
       }
    break;
  default:
     return null;
  }
}

export default combineReducers({
    rootReducer
  })
