import { combineReducers } from 'redux'
export const rootReducer   = (state, action) => {
  switch (action.type) {
    case "TOGGLE_TODO":
      if(action.id !== undefined)

        return state = {menuselected: action.id}
       else{
        state = {menuselected: "Staff"};
       return state;
       }
    break;
    case "TOGGLE_VIEW":
      if(action.id !== undefined){
        alert(state.menuselected)
        alert(action.id)
        state.viewselected = "Grid";
        state.menuselected = "Projects";
        alert(state.menuselected)
        alert(state.viewselected)
        return state;// = {viewselected: action.id}
      }else{
        alert(state.menuselected)
        state.menuselected = "Projects";
        alert(state.menuselected)
        state.viewselected = "Grid";
        alert(state.menuselected)
        alert(state.viewselected)
       return state;
       }
    break;
  default:
     return null;
  }
}

export default combineReducers({
    rootReducer
  })
